Rules.InvariantScript:
//　更新処理する
questionButtonComponent.updateAnswer();
Navigate(
    screenCanvas,
    ScreenTransition.Fade
);

ControlPropertyState.AutoRuleBindingString:
If(
    controlType = "text",
    Set(
        answerContent,
        textAnswer.Text
    ),
    Set(
        answerContent,
        Radio1.Selected.Result
    )
);
If(
    Not(
        And(
            answerContent = currentAnswer,
            skipFlag = Checkbox1.Value,
            noAnswerFlag = Checkbox3.Value
        )
    ),
With(
    {
        getAnswerRecord: LookUp(
            '仮説構築支援ツール - 回答',
            And(
                themeID = themeId,
                questionID = questionKey
            )
        ),
        categoryItem: LookUp(
            questionList,
            ID = questionKey
        ).category,
        status:0
    },
    If(
        IsBlank(getAnswerRecord),
        Patch(
            '仮説構築支援ツール - 回答',
            Defaults('仮説構築支援ツール - 回答'),
            {
                themeID: themeId,
                questionID: questionKey,
                category: categoryItem,
                answer: answerContent,
                skip_flag: Checkbox1.Value,
                no_answer_flag:Checkbox3.Value,
                status:status
            }
        );
        ,
        Patch(
            '仮説構築支援ツール - 回答',
            getAnswerRecord,
            {
                answer: answerContent,
                skip_flag: Checkbox1.Value,
                no_answer_flag: Checkbox3.Value,
                status:status
            }
        );
    )
);)

Rules.InvariantScript:
With({qRecord:LookUp(questionList, ID=qId)},
Set(
    currentTitle,
    qRecord.Title
);
Set(
    currentQuestion,
    qRecord.question_content
);

Set(
    currentPage,
    qRecord.current_question_number
);
Set(
    totalPage,
    qRecord.question_totalnumber
);
Set(
    commentary,
    qRecord.commentary
);
Set(
    example,
    qRecord.example
);
Set(
    controlType,
    qRecord.control_type
);
Set(
    currentRequired,
    qRecord.required
);
If(buttonKey="next",
//次へボタンの場合
Set(
    nextVisible,
    If(qRecord.next_question_id=0,false,true)
);
Set(
    backVisible,
    true
);,
//戻るボタンの場合
Set(
    nextVisible,
    true
);
Set(
    backVisible,
     If(qRecord.back_question_id=0,false,true)
););

Set(
    confirmVisible,
    If(qRecord.next_question_id=0,true,false)
);

With({getAnswerRecord:LookUp('仮説構築支援ツール - 回答',And(themeID=themeId,questionID=qId))},
Reset(textAnswer);
Reset(Checkbox1);
Reset(Checkbox3);
Set(
    currentAnswer,
    If(IsBlank(getAnswerRecord),"",getAnswerRecord.answer)
    );
Set(
    skipFlag,
    getAnswerRecord.skip_flag
    );
Set(
    noAnswerFlag,
    getAnswerRecord.no_answer_flag
    );
);
 

Set(questionKey,qId);
Set(
    commentaryGroupVisible,
    true
);
Set(
    exampleGroupVisible,
    false
);
If(Not(currentRequired="2"),Set(currentQuestion,Concatenate(currentQuestion,"*")))
)

If(
    controlType = "text",
    Set(
        answerContent,
        textAnswer.Text
    ),
    Set(
        answerContent,
        Radio1.Selected.Result
    )
);
If(
    Not(
        And(
            answerContent = currentAnswer,
            skipFlag = Checkbox1.Value,
            noAnswerFlag = Checkbox3.Value
        )
    ),
With(
    {
        getAnswerRecord: LookUp(
            '仮説構築支援ツール - 回答',
            And(
                themeID = themeId,
                questionID = questionKey
            )
        ),
        categoryItem: LookUp(
            questionList,
            ID = questionKey
        ).category,
        status:If(Checkbox1.Value,2,!IsBlank(answerContent),10,And(currentRequired="2",Checkbox3.Value),10,0)
    },
    If(
        IsBlank(getAnswerRecord),
        Patch(
            '仮説構築支援ツール - 回答',
            Defaults('仮説構築支援ツール - 回答'),
            {
                themeID: themeId,
                questionID: questionKey,
                category: categoryItem,
                answer: answerContent,
                skip_flag: Checkbox1.Value,
                no_answer_flag:Checkbox3.Value,
                status:status
            }
        );
        ,
        Patch(
            '仮説構築支援ツール - 回答',
            getAnswerRecord,
            {
                answer: answerContent,
                skip_flag: Checkbox1.Value,
                no_answer_flag: Checkbox3.Value,
                status:status
            }
        );
    )
);)
