ControlPropertyState.AutoRuleBindingString:
Set(nextKey,LookUp('仮想構築支援ツール - 問い', ID=qId).next_question_id);

With({qRecord:LookUp('仮想構築支援ツール - 問い', ID=nextKey)},
Set(
    currentTitle,
    qRecord.Title
);
Set(
    currentQuestion,
    qRecord.question_content
);

Set(
    currentPage,
    qRecord.current_question_number
);
Set(
    totalPage,
    qRecord.question_totalnumber
);
Set(
    commentary,
    qRecord.commentary
);
Set(
    example,
    qRecord.example
);
If(qId=0,Set(
    nextVisible,
    If(qRecord.next_question_id=0,false,true)
);Set(
    buckVisible,
    true
);,"Green");


Set(
    // 次の回答はどうやって取得するか検討する
    currentAnswer,
    galleryTheme.Selected.id1_answer
);
Set(questionKey,nextKey);
Set(
    commentaryGroupVisible,
    true
);
Set(
    exampleGroupVisible,
    false
);
)

Rules.InvariantScript:
With({qRecord:LookUp('仮説構築支援ツール - 問い', ID=qId)},
Set(
    currentTitle,
    qRecord.Title
);
Set(
    currentQuestion,
    qRecord.question_content
);

Set(
    currentPage,
    qRecord.current_question_number
);
Set(
    totalPage,
    qRecord.question_totalnumber
);
Set(
    commentary,
    qRecord.commentary
);
Set(
    example,
    qRecord.example
);
Set(
    controlType,
    qRecord.control_type
);
If(buttonKey="next",
//次へボタンの場合
Set(
    nextVisible,
    If(qRecord.next_question_id=0,false,true)
);
Set(
    buckVisible,
    true
);,
//戻るボタンの場合
Set(
    nextVisible,
    true
);
Set(
    buckVisible,
     If(qRecord.back_question_id=0,false,true)
););

Set(
    confirmVisible,
    If(qRecord.next_question_id=0,true,false)
);

With({getAnswerRecord:LookUp('仮説構築支援ツール - 回答',And(themeID=themeId,questionID=qId))},
Reset(textAnswer);
Set(
    currentAnswer,
    If(IsBlank(getAnswerRecord),"",getAnswerRecord.answer)
    );
);
 

Set(questionKey,qId);
Set(
    commentaryGroupVisible,
    true
);
Set(
    exampleGroupVisible,
    false
);
)

// ラジオボタンの場合の値を取得する処理を追加する必要有★★
If(controlType="text",Set(answerContent,textAnswer.Text),Set(answerContent,Radio1.Selected.Result));
If(Not(answerContent=currentAnswer),
With({getAnswerRecord:LookUp('仮説構築支援ツール - 回答',And(themeID=themeId,questionID=questionKey))},
//Set(getAnswerRecord,getAnswerRecord); ビューの変数から値が確認できる★★
If(IsBlank(getAnswerRecord),
Patch('仮説構築支援ツール - 回答', Defaults('仮説構築支援ツール - 回答'),{themeID:themeId,questionID:questionKey, answer: answerContent });,
Patch('仮説構築支援ツール - 回答', getAnswerRecord,{ answer: answerContent });
)));
